<?php session_start(); require_once __DIR__.'/../includes/dbconnection.php'; $err = ''; if ($_SERVER['REQUEST_METHOD']==='POST') { $user = trim($_POST['username'] ?? ''); $pass = trim($_POST['password'] ?? ''); $st = $dbh->prepare('SELECT * FROM tbladmin WHERE UserName=? LIMIT 1'); $st->execute([$user]); $adm = $st->fetch(); if ($adm && $adm['Password'] === md5($pass)) { $_SESSION['admin_id'] = $adm['ID']; $_SESSION['admin_name'] = $adm['AdminName']; session_regenerate_id(true); header('Location: dashboard.php'); exit; } else { $err = 'Invalid credentials'; } } ?> <!doctype html> <html> <head> <meta charset="utf-8"><meta name="viewport" content="width=device-width, initial-scale=1"> <title>Admin Login - MCGS</title> <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css"> </head> <body class="bg-light"> <div class="container py-5"> <div class="row justify-content-center"> <div class="col-md-4"> <div class="card shadow-sm"> <div class="card-body"> <h4 class="mb-3">Admin Login</h4> <?php if($err): ?><div class="alert alert-danger"><?php echo htmlspecialchars($err); ?></div><?php endif; ?> <form method="post"> <div class="mb-3"> <label class="form-label">Username</label> <input name="username" class="form-control" required> </div> <div class="mb-3"> <label class="form-label">Password</label> <input name="password" type="password" class="form-control" required> </div> <button class="btn btn-primary w-100">Login</button> </form> <div class="mt-3 text-muted small">Default: admin / admin123</div> </div> </div> </div> </div> </div> </body></html>