<?php require_once __DIR__.'/../includes/dbconnection.php'; require_once __DIR__.'/header.php'; $id = (int)($_GET['id'] ?? 0); $st = $dbh->prepare('SELECT * FROM tblmedicalcard WHERE ID=?'); $st->execute([$id]); $r = $st->fetch(); if (!$r) { echo '<div class="alert alert-danger">Not found</div>'; require 'footer.php'; exit; } ?> <div class="card p-4"> <div class="d-flex justify-content-between align-items-center"> <h4>Medical Card</h4> <button class="btn btn-outline-secondary" onclick="window.print()">Print</button> </div> <hr> <div class="row"> <div class="col-md-3"> <?php if ($r['ProfileImage']): ?> <img class="img-thumbnail" src="../uploads/<?php echo htmlspecialchars($r['ProfileImage']); ?>" alt="Photo"> <?php else: ?> <div class="bg-secondary text-white text-center p-5">No Photo</div> <?php endif; ?> </div> <div class="col-md-9"> <h5><?php echo htmlspecialchars($r['FullName']); ?> <small class="text-muted">#<?php echo htmlspecialchars($r['RefNumber']); ?></small></h5> <p><strong>Phone:</strong> <?php echo htmlspecialchars($r['ContactNumber']); ?> | <strong>Email:</strong> <?php echo htmlspecialchars($r['Email']); ?></p> <p><strong>Blood Group:</strong> <?php echo htmlspecialchars($r['BloodGroup']); ?> | <strong>Gender:</strong> <?php echo htmlspecialchars($r['Gender']); ?></p> <p><strong>Address:</strong> <?php echo htmlspecialchars($r['Address']); ?></p> <p><strong>Medical Conditions:</strong> <?php echo htmlspecialchars($r['MedicalCond']); ?></p> <p><strong>Issued:</strong> <?php echo htmlspecialchars($r['IssuedDate']); ?> | <strong>Valid Upto:</strong> <?php echo htmlspecialchars($r['ValidDate']); ?></p> <p class="text-muted"><strong>Created:</strong> <?php echo htmlspecialchars($r['CreationDate']); ?></p> </div> </div> </div> <?php require_once __DIR__.'/footer.php'; ?>