<?php require_once __DIR__.'/includes/dbconnection.php'; require_once __DIR__.'/includes/functions.php'; require_once __DIR__.'/includes/header.php'; $msg = ''; if ($_SERVER['REQUEST_METHOD']==='POST') { $name = post('name'); $email = post('email'); $message = post('message'); if ($name && filter_var($email, FILTER_VALIDATE_EMAIL) && $message) { $st = $dbh->prepare('INSERT INTO tblcontact(Name,Email,Message) VALUES(?,?,?)'); $st->execute([$name,$email,$message]); $msg = 'Your enquiry has been sent.'; } else { $msg = 'Please fill all fields correctly.'; } } $st = $dbh->prepare("SELECT * FROM tblpage WHERE PageType='contactus' LIMIT 1"); $st->execute(); $contact = $st->fetch(); ?> <h2>Contact Us</h2> <?php if ($msg): ?><div class="alert alert-info"><?php echo e($msg); ?></div><?php endif; ?> <div class="row"> <div class="col-md-6"> <form method="post" class="card card-body"> <div class="mb-3"> <label class="form-label">Name</label> <input name="name" class="form-control" required> </div> <div class="mb-3"> <label class="form-label">Email</label> <input name="email" type="email" class="form-control" required> </div> <div class="mb-3"> <label class="form-label">Message</label> <textarea name="message" class="form-control" rows="5" required></textarea> </div> <button class="btn btn-primary">Send</button> </form> </div> <div class="col-md-6"> <div class="card card-body"> <h5><?php echo e($contact['PageTitle'] ?? 'Contact'); ?></h5> <p><?php echo nl2br(e($contact['PageDescription'] ?? '')); ?></p> <p><strong>Email:</strong> <?php echo e($contact['Email'] ?? ''); ?></p> <p><strong>Phone:</strong> <?php echo e($contact['MobileNumber'] ?? ''); ?></p> </div> </div> </div> <?php require_once __DIR__.'/includes/footer.php'; ?>